#' Outlier Detection
#'
#' 
#' @param data .
#' @param vars .
#' @param groups .
#' @param threshold .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$thresholdInfo} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$outlierTable} \tab \tab \tab \tab \tab a table \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$outlierTable$asDF}
#'
#' \code{as.data.frame(results$outlierTable)}
#'
#' @export
ModifiedZ <- function(
    data,
    vars,
    groups,
    threshold = 3.5) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("ModifiedZ requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(groups)) groups <- jmvcore::resolveQuo(jmvcore::enquo(groups))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(groups), groups, NULL))


    options <- ModifiedZOptions$new(
        vars = vars,
        groups = groups,
        threshold = threshold)

    analysis <- ModifiedZClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

